import '../css/App.css';
import { useState,useEffect,useContext } from 'react';
import UserContext from './UseContext';
const Header = () => {
    const {username} = useContext(UserContext);
    const [identify, setIdentify] = useState('');
    function findMenu(e){
        const Menu = document.querySelectorAll('#${e}');
        Menu.forEach(element=>{
            element.style.display="block";
        })
    }
    useEffect(()=>{
        const iden = username.substring(0,2);
        console.log(iden);
        if(iden=="12"){
        const nurMenu = document.querySelectorAll('#nurMenu');
        nurMenu.forEach(nur=>{
            nur.style.display="block";
        })
        } else if(iden=="11"){

           findMenu('docMenu');
        }

    })

    return(
        <>
        <div className="header">
            <img className="headerLogo" src="img/logo2.png"/>
            <div className="headerLMenu">
            <a id="a" href="/organ"><h4>조직도</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="nurMenu">입퇴원 조회</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="nurMenu">처방 일지</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="nurMenu">병동 조회</h4></a>


            <a><h4 id="docMenu">담당 환자</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="docMenu">외래 진료</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="docMenu">입원 진료</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;
            <a><h4 id="docMenu">수술 진료</h4></a>&nbsp;&nbsp;&nbsp;&nbsp;

            </div>
            <div className="headerRMenu">
            <img className="headerAlarm headerIcon" src="img/alaram.png"/>&nbsp;&nbsp;&nbsp;
            <img className="headerSchedule headerIcon" src="img/schedule.png"/>&nbsp;&nbsp;&nbsp;
            <img className="headerLogout headerIcon" src="img/logout.png"/>
            </div>
        </div>
        </>
    )
}
export default Header;
