import React, {useState} from 'react';
import "../css/NurDailyPrescription.css";
const PrescModal = () =>{
    const [isChecked, setIsChecked] = useState(false);
    const handleClick=()=>{
        setIsChecked(!isChecked);
    }
    return(
        <div className="PrescModalbox">
            <br/>
            <div className="boxHeader">
                <h3 id="boxHeader" style={{margin:"0 auto"}}>처방 상태 변경</h3>
            </div>
            <br/>
            <div className="prescContent">
            <label className={isChecked ? 'labelL radio-checked':'labelL'}  style={{backgroundColor:"lightgray"}}for="presX1">
                        <input type="radio" id="presX1" style={{ height:"20px",display:"none", zIndex:"9999"}} onClick={handleClick}/>미투약
                            </label>

                        <label className={isChecked ? 'labelL radio-checked':'labelL'} style={{backgroundColor:"#F7CE7E"}} for="presX1">
                        <input type="radio" id="presX1" style={{ height:"20px",display:"none", zIndex:"9999"}} onClick={handleClick}/>정상 투약
                            </label>


                        <label className={isChecked ? 'labelL radio-checked':'labelL'} style={{backgroundColor:"#609E66"}} for="presX2">
                        <input type="radio" id="presX2" style={{ height:"20px",display:"none", zIndex:"9999"}} onClick={handleClick}/>투약 X - 반환 가능
                            </label>

                        <label className={isChecked ? 'labelL radio-checked':'labelL'} style={{backgroundColor:"red", marginBottom:"15px"}} for="presX3">
                        <input type="radio" id="presX3" style={{ height:"20px",display:"none", zIndex:"9999"}} onClick={handleClick}/>투약 X - 반환 불가
                            </label>
                            </div>               
                            <button id="button1" style={{margin:"0"}}>저장</button>
                </div>
    )
}

export default PrescModal;
